import {
  BrowserRouter,
  Routes,
  Route,
  //   useSearchParams,
} from "react-router-dom";
import React, { useEffect, useState } from "react";
import uuid from "react-uuid";
import Main from "./Main";
import Nav from "./Nav";
import Sidebar from "./Sidebar";

function App() {
  const [isVisible, setIsVisible] = useState(true);
  const [notes, setNotes] = useState(
    localStorage.notes ? JSON.parse(localStorage.notes) : []
  );
  const [edit, setEdit] = useState(
    localStorage.edit ? JSON.parse(localStorage.edit) : []
  );

  useEffect(() => {
    localStorage.setItem("notes", JSON.stringify(notes));
  }, [notes]);

  useEffect(() => {
    localStorage.setItem("edits", JSON.stringify(edit));
  }, [edit]);

  const onAddNote = () => {
    const newNote = {
      id: uuid(),
      title: "Untitled",
      //   date: Date.now(),
      body: "...",
    };

    setNotes([newNote, ...notes]);
    setActiveNote(newNote.id);
  };

  const onDeleteNote = (deleteID) => {
    const answer = window.confirm("Are you sure?");
    if (answer) {
      setNotes(notes.filter((note) => note.id !== deleteID));
    }
  };

  /* get the current note */
  const [activeNote, setActiveNote] = useState(false);
  const getActiveNote = () => {
    return notes.find((note) => note.id === activeNote);
  };

  const [isSaved, setIsSaved] = useState(false);

  const editNote = (updatedNote) => {
    const updatedNotesList = notes.map((note) => {
      if (note.id === activeNote) {
        return updatedNote;
      }
      return note;
    });

    setNotes(updatedNotesList);
  };

  return (
    <BrowserRouter>
      <div className="main">
        <div className="header-header">
          <Route
            path="/"
            element={<Nav setIsVisible={setIsVisible} isVisible={isVisible} />}
          />
        </div>

        <div className="App">
          {isVisible && (
            <Route
              path="/notes"
              element={
                <Sidebar
                  notes={notes}
                  onAddNote={onAddNote}
                  activeNote={activeNote}
                  setActiveNote={setActiveNote}
                />
              }
            />
          )}
          <Route
            path="/edits"
            element={
              <Main
                notes={notes}
                edit={edit}
                setEdit={setEdit}
                onDeleteNote={onDeleteNote}
                activeNote={getActiveNote()}
                editNote={editNote}
              />
            }
          />
        </div>
      </div>
    </BrowserRouter>
  );
}

export default App;
