import { useNavigate, useParams } from "react-router-dom";

function Sidebar({ notes, onAddNote, activeNote, setActiveNote }) {
  const navigate = useNavigate();

  const emptyNote = () => {
    if (notes === null) {
      <div>No Note Yet</div>;
    }
    {
      notes.map((note) => (
        <div
          className={`notes-list ${
            note.id === activeNote && "notes-list active"
          }`}
          onClick={() => {
            setActiveNote(note.id);
            navigate(`/notes/` + note.id);
          }}
        >
          <div className="notes-title">
            <p id="note-title">
              <strong>{note.title}</strong>
            </p>
            <p id="date-style">Date</p>
            {note.body && note.body.substring(0, 100) + "..."}
          </div>
        </div>
      ));
    }
  };

  return (
    <div className="notes-sidebar">
      <div className="notes-menu">
        <h1>Notes </h1>
        <button className="add" onClick={onAddNote}>
          +
        </button>
      </div>
      <div className="notes-lists">{emptyNote}</div>
    </div>
  );
}

export default Sidebar;
