import React, { useState } from "react";

function View({ notes }) {
  if (!activeNote) {
    return <div className="no-notes"> Select a note, or create a new one</div>;
  }
  return (
    <div className="main-nav">
      <div className="note-edit">
        <div className="with-buttons">
          <div className="title-date">
            <input id="text-title" type="text" value={notes.title} />
            <input type="datetime-local" />
          </div>
          <div className="buttons">
            <button>Edit</button>
            <button onClick={() => onDeleteNote(activeNote.id)}>Delete</button>
          </div>
        </div>
      </div>
      <div className="quill-container">
        <ReactQuill className="quill-editor" theme="snow" value={notes.body} />
      </div>
    </div>
  );
}
export default View;
