import { useNavigate, useParams } from "react-router-dom";

function Sidebar({ notes, onAddNote, activeNote, setActiveNote }) {
  const navigate = useNavigate();
  const trimmedBody = "";
  const onClickSet = (note) => {
    setActiveNote(note.id);
    navigate(`/notes/` + note.id);
    trimmedBody = note.body.substring(0, 100) + "...";
  };
  return (
    <div className="notes-sidebar">
      <div className="notes-menu">
        <h1>Notes </h1>
        <button className="add" onClick={onAddNote}>
          +
        </button>
      </div>
      <div className="notes-lists">
        {notes.map((note) => (
          <div
            className={`notes-list ${
              note.id === activeNote && "notes-list active"
            }`}
            onClick={onClickSet(note)}
          >
            <div className="notes-title">
              <p id="note-title">
                <strong>{note.title}</strong>
              </p>
              <p id="date-style">{note.date}</p>
              <div dangerouslySetInnerHTML={{ __html: trimmedBody }}></div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Sidebar;
