import React, { useState } from "react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";

function Main({ notes, onDeleteNote, activeNote, editNote }) {
  const [value, setValue] = useState("Your Note Here");

  const options = {
    year: "numeric",
    month: "long",
    day: "numeric",
    hour: "numeric",
    minute: "numeric",
  };

  const formatDate = (when) => {
    const formatted = new Date(when).toLocaleString("en-US", options);
    if (formatted === "Invalid Date") {
      return "";
    }
    return formatted;
  };

  const editField = (editValue) => {
    editNote({
      id: activeNote.id,
      title: editValue,
      body: value.replace(/<[^>]*>?/gm, ""),
    });
  };

  const bodyChange = (newValue) => {
    setValue(newValue);
    editNote({
      id: activeNote.id,
      title: activeNote.title,
      body: newValue,
    });
  };

  if (!activeNote) {
    return <div className="no-notes"> Select a note, or create a new one</div>;
  }

  return (
    <div className="main">
      <div className="note-edit">
        <div className="with-buttons">
          <div className="title-date">
            <input
              id="text-title"
              type="text"
              value={activeNote.title}
              onChange={(e) => editField(e.target.value)}
            />
            <input type="datetime-local" value={formatDate()} />
          </div>
          <div className="buttons">
            <button>Save</button>
            <button onClick={() => onDeleteNote(activeNote.id)}>Delete</button>
          </div>
        </div>
        <ReactQuill theme="snow" value={value} onChange={bodyChange} />
      </div>
    </div>
  );
}

export default Main;
