import React, { useState, useRef } from "react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
import { useNavigate } from "react-router-dom";

function Edit({ notes, onDeleteNote, activeNote, editNote, edit, setEdit }) {
  const [value, setValue] = useState(activeNote.body);
  const [title, setTitle] = useState(activeNote.title);
  const [date, setDate] = useState(activeNote.date);

  const options = {
    year: "numeric",
    month: "long",
    day: "numeric",
    hour: "numeric",
    minute: "numeric",
  };

  const formatDate = (when) => {
    const formatted = new Date(when).toLocaleString("en-US", options);
    if (formatted === "Invalid Date") {
      return "";
    }
    return formatted;
  };

  // const quillRef = useRef(null);
  // const quillElement = quillRef.current.getEditor().root;

  const navigate = useNavigate();
  // const { id } = useParams();

  const savedNote = () => {
    activeNote.title = title;
    activeNote.body = value;
    activeNote.date = date;
    navigate(`/notes/` + activeNote.id);
  };

  const getDate = (event) => {
    const dateValue = event.target.value;
    setDate(formatDate(dateValue));
  };

  if (!activeNote) {
    return <div className="no-notes"> Select a note, or create a new one</div>;
  }
  return (
    <div className="main-nav">
      <div className="note-edit">
        <div className="with-buttons">
          <div className="title-date">
            <input
              id="text-title"
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
            <input type="datetime-local" onChange={getDate} />
          </div>
          <div className="buttons">
            <button onClick={savedNote}>Save</button>
            <button onClick={() => onDeleteNote(activeNote.id)}>Delete</button>
          </div>
        </div>
      </div>
      <div className="quill-container">
        <ReactQuill
          className="quill-editor"
          theme="snow"
          value={value}
          onChange={setValue}
          readOnly={false}
        />
      </div>
    </div>
  );
}

export default Edit;
