function Sidebar({ notes, onAddNote, activeNote, setActiveNote }) {
  return (
    <div className="main">
      <div className="header-header">
        <div className="header">
          <button
            id="menu-button"
            onClick={() => {
              setIsVisible(!isVisible);
            }}
          >
            {" "}
            &#9776;
          </button>
          <div className="title">
            <h1>Lotion</h1>
            <p> Like Notion, but worse.</p>
          </div>
        </div>
      </div>
      <div className="App">
        {isVisible && (
          <div className="notes-sidebar">
            <div className="notes-menu">
              <h1>Notes </h1>
              <button className="add" onClick={onAddNote}>
                +
              </button>
            </div>
            <div className="notes-lists">
              {notes.map((note) => (
                <div
                  className={`notes-list ${
                    note.id === activeNote && "notes-list active"
                  }`}
                  onClick={() => setActiveNote(note.id)}
                >
                  <div className="notes-title">
                    <p id="note-title">
                      <strong>{note.title}</strong>
                    </p>
                    <p id="date-style">Date</p>
                    {note.body && note.body.substring(0, 100) + "..."}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
        ;
      </div>
    </div>
  );
}

export default Sidebar;
