import React, { useState } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

function Main() {
  const [value, setValue] = useState('');
  // const [notes, setNotes] = useState("Untitled");
  const options = {
    year: "numeric",
    month: "long",
    day: "numeric",
    hour: "numeric",
    minute: "numeric",
  };

  const formatDate = (when) => {
      const formatted = new Date(when).toLocaleString("en-US", options);
      if (formatted === "Invalid Date") {
          return "";
      }
      return formatted;
  };

  function Button() {
        // let newNote = document.createElement("div");
        // newNote.classList.add("new-notes");
        // newNote.innerText = "untitled";
        // document.getElementById("main-menu").appendChild(newNote);
        let board = document.getElementById("main-menu");
        board.classList.remove("hidden");

  }

  return (
    <div className="main">
      <aside id="notes">
        <div id="notes-menu">
          <div id="notes-title"><h1>Notes </h1></div>
          <div id="add"><button onClick={Button()}>+</button></div>
        </div>
        <div>
          <p>contents</p>
        </div>
      </aside>
      <div className="hidden" id="main-container">
        <div id="main-menu">
          <div><input id="text-title" type="text" value={"Untitled"}/></div>
          <div><input type="datetime-local" value={formatDate()}/></div>
        </div>
          <ReactQuill theme="snow" value={value} onChange={setValue} />
      </div>
    </div>
  );
}

export default Main;
