import React, { useState } from "react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";

function Main({ notes, onDeleteNote, activeNote, editNote }) {
  const [value, setValue] = useState("");
  // const [body, setBody] = useState(activeNote.body);
  const [title, setTitle] = useState(activeNote.title);

  const options = {
    year: "numeric",
    month: "long",
    day: "numeric",
    hour: "numeric",
    minute: "numeric",
  };

  const formatDate = (when) => {
    const formatted = new Date(when).toLocaleString("en-US", options);
    if (formatted === "Invalid Date") {
      return "";
    }
    return formatted;
  };

  const titleChange = (newValue) => {
    setTitle(newValue);
  };

  // const removeTags = (text) => {
  //   return text.replace(/(<[^>]+>)/gi, "");
  // };

  // const editField = (newValue) => {
  //   if (save) {
  //     editNote({
  //       id: activeNote.id,
  //       title: newValue,
  //       // date: activeNote.date,
  //       body: newValue,
  //     });
  //     activeNote.title = newValue;
  //   }
  // };

  const bodyChange = (newValue) => {
    setValue(newValue);
    // if (save) {
    //   editNote({
    //     id: activeNote.id,
    //     title: activeNote.title,
    //     // date: activeNote.date,
    //     body: newValue,
    //   });
    // }
  };

  const savedNote = () => {
    editNote({
      id: activeNote.id,
      title: title,
      body: body,
    });
  };

  if (!activeNote) {
    return <div className="no-notes"> Select a note, or create a new one</div>;
  }

  return (
    <div className="main-nav">
      <div className="note-edit">
        <div className="with-buttons">
          <div className="title-date">
            <input
              id="text-title"
              type="text"
              value={title}
              onChange={(e) => titleChange(e.target.value)}
            />
            <input
              type="datetime-local"
              value={formatDate(activeNote.date)}
              // onChange={(e) => console.log(e.target.value)}
            />
          </div>
          <div className="buttons">
            <button onClick={savedNote}>Save</button>
            <button onClick={() => onDeleteNote(activeNote.id)}>Delete</button>
          </div>
        </div>
      </div>
      <div className="quill-container">
        <ReactQuill
          className="quill-editor"
          theme="snow"
          placeholder="Your Note Here"
          value={value}
          onChange={bodyChange}
        />
      </div>
    </div>
  );
}

export default Main;
