import React, { useState } from "react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";

function Main() {
  const [value, setValue] = useState("");

  const options = {
    year: "numeric",
    month: "long",
    day: "numeric",
    hour: "numeric",
    minute: "numeric",
  };

  const formatDate = (when) => {
    const formatted = new Date(when).toLocaleString("en-US", options);
    if (formatted === "Invalid Date") {
      return "";
    }
    return formatted;
  };

  const [divs, setDivs] = useState([]);

  const [title, setTitle] = useState("Untitled");
  const titledEntered = (event) => {
    if (event.key.toLowerCase() === "enter") {
      const value = event.target.value;
      setTitle(value);
    }
  };

  const [content, setContent] = useState(
    "<p>Select a note, or create a new one.</p>"
  );

  const handleClick = () => {
    setDivs([...divs, <div>{title}</div>]);
    setContent(
      "<div><input id='text-title' type='text' onKeyUp={titledEntered} /><input type='datetime-local' value={formatDate()} /></div><ReactQuill theme='snow' value={value} onChange={setValue} />"
    );
  };

  return (
    <div className="main">
      <aside id="notes">
        <div id="notes-menu">
          <div id="notes-title">
            <h1>Notes </h1>
          </div>
          <div id="add">
            <button onClick={handleClick}>+</button>
          </div>
        </div>
        <div>
          {divs.map((d, i) => (
            <div key={i}>{d}</div>
          ))}
        </div>
      </aside>
      <div id="main-container">{content}</div>
    </div>
  );
}

export default Main;
