import React, { useState } from "react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";

function Main({ notes, onDeleteNote }) {
  const [value, setValue] = useState("Your Note Here");

  const options = {
    year: "numeric",
    month: "long",
    day: "numeric",
    hour: "numeric",
    minute: "numeric",
  };

  const formatDate = (when) => {
    const formatted = new Date(when).toLocaleString("en-US", options);
    if (formatted === "Invalid Date") {
      return "";
    }
    return formatted;
  };
  return (
    <div className="main">
      {/* <p>Select a note, or create a new one.</p> */}
      <div className="note-edit">
        <div className="with-buttons">
          <div className="title-date">
            <input id="text-title" type="text" />
            <input type="datetime-local" value={formatDate()} />
          </div>
          <div className="buttons">
            <button>Save</button>
            <button onClick={() => onDeleteNote(notes.id)}>Delete</button>
          </div>
        </div>
        <ReactQuill theme="snow" value={value} onChange={setValue} />
      </div>
    </div>
  );
}

export default Main;
