import React, { useEffect, useState } from "react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";

function Main({ notes, onDeleteNote, activeNote, editNote }) {
  const [value, setValue] = useState("Your Note Here");
  const [editorValue, setEditorValue] = useState("");

  const options = {
    year: "numeric",
    month: "long",
    day: "numeric",
    hour: "numeric",
    minute: "numeric",
  };

  const formatDate = (when) => {
    const formatted = new Date(when).toLocaleString("en-US", options);
    if (formatted === "Invalid Date") {
      return "";
    }
    return formatted;
  };

  useEffect(() => {
    setEditorValue(value);
  }, [value]);

  const editField = (editValue) => {
    editNote({
      id: activeNote.id,
      title: editValue,
      body: editorValue,
    });

    const bodyChange = (newValue) => {
      setValue(newValue);
    };

    const handleSave = () => {
      setValue(editorValue);
      editNote({
        id: activeNote.id,
        title: activeNote.title,
        body: editorValue,
      });
    };

    if (!activeNote) {
      return (
        <div className="no-notes"> Select a note, or create a new one</div>
      );
    }

    return (
      <div className="main-nav">
        <div className="note-edit">
          <div className="with-buttons">
            <div className="title-date">
              <input
                id="text-title"
                type="text"
                value={activeNote.title}
                onChange={(e) => editField(e.target.value)}
              />
              <input type="datetime-local" value={formatDate()} />
            </div>
            <div className="buttons">
              <button onClick={handleSave}>Save</button>
              <button onClick={() => onDeleteNote(activeNote.id)}>
                Delete
              </button>
            </div>
          </div>
          <ReactQuill theme="snow" value={editValue} onChange={bodyChange} />
        </div>
      </div>
    );
  };
}

export default Main;
